import CartContext from "./cart-context.js";
import { useReducer } from "react";

const defaultCartState = {
  items: [],
  totalAmount: 0,
  addItem: (item) => {},
  removeItem: (id) => {},
};

const cartReducer = (state, action) => {
  console.log("state, action", state, action);
  // if (action.type === "ADD") {
  //   let totalItems = state.items.concat(action.item);
  //   let totalAmount =
  //     state.totalAmount + action.item.price * action.item.amount;
  //   return {
  //     items: totalItems,
  //     totalAmount: totalAmount,
  //   };
  // }
  //else if (action.type === "REMOVE") {
  //   let totalItems = state.items.filter((item) => item.id !== action.id);
  //   let totalAmount = totalItems.reduce((curNumber, item) => {
  //     return curNumber + item.price * item.amount;
  //   });
  //   return {
  //     items: totalItems,
  //     totalAmount: totalAmount,
  //   };
  // }
  return state;
};

const CartContextProvider = (props) => {
  const [cartState, dispatchCartAction] = useReducer(
    cartReducer,
    defaultCartState
  );

  const addItemCartHandler = (item) => {
    console.log("add item handler", item);
    dispatchCartAction({
      type: "ADD",
      item: item,
    });
    // console.log(item);
  };
  const removeCatItemHander = (id) => {
    dispatchCartAction({
      type: "REMOVE",
      id: id,
    });
  };
  const CartContextValue = {
    items: cartState.items,
    totalAmount: cartState.totalAmount,
    addItem: addItemCartHandler,
    removeItem: removeCatItemHander,
  };

  return (
    <CartContext.Provider value={CartContextValue}>
      {" "}
      {props.children}{" "}
    </CartContext.Provider>
  );
};
export default CartContextProvider;
