    //     console.log(response);
    //   })
    //   .catch((error) => {
    //     // handle error
    //     console.log(error);
    //   })
    //   .finally(() => {
    //     // always executed
    //   });
    console.log("fetchMovieHandler");
  };

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMovieHandler}>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={moviesApi} />
      </section>
    </React.Fragment>
  );
}

export default App;
