import React, { useState } from "react";

import MoviesList from "./components/MoviesList";
import "./App.css";
import axios from "axios";
import moment from "moment";

function App() {
  // const dummyMovies = [
  //   {
  //     id: 1,
  //     title: "Some Dummy Movie",
  //     openingText: "This is the opening text of the movie",
  //     releaseDate: "2021-05-18",
  //   },
  //   {
  //     id: 2,
  //     title: "Some Dummy Movie 2",
  //     openingText: "This is the second opening text of the movie",
  //     releaseDate: "2021-05-19",
  //   },
  // ];

  const [moviesApi, setMoviesApi] = useState([]);

  const fetchMovieHandler = () => {
    axios
      .get("https://jsonplaceholder.typicode.com/posts")
      .then((response) => {
        // handle success
        //current date set in array object
        response.data.forEach((d) => {
          d.releaseDate = moment().format("MMM Do YY");
          d.openingText = d.body;
        });
        setMoviesApi([...response.data]);
        console.log(response);
      })
      .catch((error) => {
        // handle error
        console.log(error);
      })
      .finally(() => {
        // always executed
      });
    console.log("fetchMovieHandler");
  };

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMovieHandler}>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={moviesApi} />
      </section>
    </React.Fragment>
  );
}

export default App;
