    //   .catch((error) => {
    //     // handle error
    //     console.log(error);
    //   })
    //   .finally(() => {
    //     // always executed
    //   });
    console.log("fetchMovieHandler");
  };

  return (
    <React.Fragment>
      <section>
        <button onClick={fetchMovieHandler}>Fetch Movies</button>
      </section>
      <section>
        {!isLoading && <MoviesList movies={moviesApi} />}
        {isLoading && <p>Loading...</p>}
        {error && <p>{error}</p>}
      </section>
    </React.Fragment>
  );
}

export default App;
