import { useReducer } from 'react';

const inputReducerFun = (state, action) => {
    if (action.type === 'inputValue') {
        return {
            ...state,
            inputValue: action.value
        }
    }
    else if (action.type === 'enterIsTuch') {
        return {
            ...state,
            enterIsTuch: action.value
        }
    } else {
        return {
            inputValue: '',
            enterIsTuch: false
        }
    }
}
const useFormInput = (validateData) => {
    // const [inputValue, setinputValue] = useState('')
    // const [enterIsTuch, setEnterIsTuch] = useState(false)

    const [getValue, dispatchGetValue] = useReducer(inputReducerFun, {
        inputValue: '',
        enterIsTuch: false
     })
  
    let isValidCheck = validateData(getValue.inputValue);

    

    if (getValue.enterIsTuch && !isValidCheck) {
      isValidCheck = false
    } else {
      isValidCheck = true
    }
  
    // console.log('isValid',isValidCheck, inputValue, enterIsTuch)
    const enterInputHandler = (event) => {
        dispatchGetValue({
            type: 'inputValue',
            value: event.target.value
        })
    }
    const enterIsTuchHandler = (event) => {
    
        dispatchGetValue({
            type: 'enterIsTuch',
            value: true
        })
    }
    const reset = () => {
        dispatchGetValue({
            type:''
        })
    }
    return {
        getValue,
        dispatchGetValue,
    
        isValidCheck,
        enterInputHandler,
        enterIsTuchHandler,
        reset
    }
}

export default  useFormInput;
