import { useState, useReducer } from 'react';

const inputReducerFun = (state, action) => {
    if (action.type === 'enterValue') {
        return {
            ...state,
            enterValue: action.value
        }
    }
    else if (action.type === 'enterIsTuch') {
        return {
            ...state,
            enterIsTuch: action.value
        }
    } else {
        return {
            enterValue: '',
            enterIsTuch: false
        }
    }
}
const useFormInput = (validateData) => {
    // const [enterValue, setEnterValue] = useState('')
    // const [enterIsTuch, setEnterIsTuch] = useState(false)

    const [getValue, dispatchGetValue] = useReducer(inputReducerFun, {
        enterValue: '',
        enterIsTuch: false
     })
  
    let isValidCheck = validateData(getValue.enterValue);

    

    if (getValue.enterIsTuch && !isValidCheck) {
      isValidCheck = false
    } else {
      isValidCheck = true
    }
  
    // console.log('isValid',isValidCheck, enterValue, enterIsTuch)
    const enterInputHandler = (event) => {
        dispatchGetValue({
            type: 'enterValue',
            value: event.target.value
        })
    }
    const enterIsTuchHandler = (event) => {
    
        dispatchGetValue({
            type: 'enterIsTuch',
            value: true
        })
    }
    const reset = () => {
        dispatchGetValue({
            type:''
        })
    }
    return {
        getValue,
        setEnterValue,
        enterIsTuch,
        setEnterIsTuch,
        isValidCheck,
        enterInputHandler,
        enterIsTuchHandler,
        reset
    }
}

export default  useFormInput;
