import React, {useState} from 'react'

const BasicForm = (props) => { 
  const [enterName, setEnterName] = useState('')
  const [enterIsTuch, setEnterIsTuch] = useState(false)

  let isValidCheck = enterName !== ''
  if (enterIsTuch && !isValidCheck) {
    isValidCheck = false
  } else {
    isValidCheck = true
  }

  console.log('isValid',isValidCheck, enterName, enterIsTuch)
  const enterInputHandler = (event) => {
    setEnterName(event.target.value)
  }
  const enterIsTuchHandler = (event) => {
    setEnterIsTuch(true)
  }
  return (
    <form>
      <div className='control-group'>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' onInput={enterInputHandler}
          onBlur={enterIsTuchHandler}
            value={enterName} />
        </div>
        <div className='form-control'>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' />
        </div>
      </div>
      <div className='form-control'>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name' />
      </div>
      <div className='form-actions'>
        <button>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
